	(2, 'CP1L SERI', 'CP1L', '<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-size:12.0pt">Chi tiết sản phẩm:</span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">PLC đa năng hơn d&ograve;ng CP1E hỗ trợ tối đa 180 I/O&nbsp;</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Bộ nhớ chương tr&igrave;nh: 10Ksteps (EEPROM)</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">V&ugrave;ng nhớ dữ liệu DM: 32Kwords</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Số lượng timers / counters: 4096 timers; 4096 counters</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Tốc độ xử l&lrm;&yacute;: Lệnh cơ bản (LD): 0.55 &micro;s min.; Lệnh cao cấp (Mov): 4.10 &micro;s min.</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Ng&otilde; v&agrave;o tốc độ cao: 4 counters, 2 axes (24-VDC input) 4 inputs: Differential phases (4x), 50 kHzSingle-phase (pulse plus direction, up/down, increment); 100kHzValue range: 32 bits; Linear mode or ring modeInterrupts: Target value comparison or range comparison</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Ng&otilde; v&agrave;o interrupt: 2 inputs (Response frequency: 5 kHz max. for all interrupt inputs), 16 bits Up or down counters</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Ng&otilde; v&agrave;o t&aacute;c động nhanh: 6 points (Min. input pulse width: 50 &mu;s max.)</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Ng&otilde; ra xung (transistor output): Trapezoidal or S-curve acceleration and deceleration (Duty ratio: 50% fixed); 2 outputs, 1 Hz to 100 kHz (CCW/CW or pulse plus direction)</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Ng&otilde; ra xung điều chế&nbsp; (PWM): Duty ratio: 0.0% to 100.0% (specified in increments of 0.1% or 1%)</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Trang bị sẵn cổng USB 2.0 kết nối m&aacute;y t&iacute;nh</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">C&oacute; thể mở rộng th&ecirc;m cổng truyền th&ocirc;ng RS232C hoặc RS422/RS485</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Giao thức truyền th&ocirc;ng: Host Link; 1:N NT Link; No-protocol mode; Serial PLC Link Slave, Serial PLC Link Master; Modbus-RTU Easy Master</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Trang bị đồng hồ thực (clock)</span></span></span></li>\r\n	<li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt">Ti&ecirc;u chuẩn: EC, EMC (zone B), JIS</span></span></span></li>\r\n</ul>', '<p>asdasda</p>', 1, 1, '/uploads/cke/CP1L.png', 'https://phanthinh.com.vn', 2, '2018-09-04 17:34:14', '2018-09-22 06:05:49', 1, 1),
	(3, 'CP1W * ( Option CP1E/L/H)', 'CP1W', NULL, '<ul>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">CP1W * l&agrave; c&aacute;c module chức năng hỗ trợ cho d&ograve;ng CP1E/CP1L/CP1H&nbsp;</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">CP1W * gồm chức năng RS232/RS485/Ethernet, mở rộng In/Out, chức năng nhiệt độ, Analog,....</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">CP1E 10, 14, 20 In/Out kh&ocirc;ng hỗ trợ CP1W</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">CP1E/CP1L hỗ trợ tối đa 3 CP1W(In/Out), Nhỏ hơn 7 word Input v&agrave; 7 word Output</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">CP1H hỗ trợ&nbsp; tối đa 7 module CP1W(In/out&nbsp;v&agrave; cần CP1W-CN811 cho module thứ tư), Nhỏ hơn 15 word Input v&agrave; 15 word Output</span></span></li>\r\n	<li><span style="font-size:14px"><a href="https://drive.google.com/open?id=1S1ZjnDzxUyvE-Sg4gGHlJYG1xWwAxcm7"><span style="color:#f39c12">Download t&agrave;i liệu</span></a></span></li>\r\n</ul>', 1, 1, '/uploads/cke/cp1w(1).jpg', 'https://phanthinh.com.vn', 4, '2018-09-08 08:48:43', '2018-09-23 04:05:18', 0, 1),
	(4, 'CP1H *', 'CP1H', '<ul>\r\n	<li style="text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,sans-serif"><span style="background-color:white"><span style="color:#2980b9">Giao thức truyền th&ocirc;ng: Host Link; 1:N NT Link; No-protocol mode; Serial PLC Link Slave, Serial PLC Link Master; Modbus-RTU Easy Master</span></span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,sans-serif"><span style="background-color:white"><span style="color:#2980b9">Ng&otilde; ra xung (transistor output): Trapezoidal or S-curve acceleration and deceleration (Duty ratio: 50% fixed); 2 outputs, 1 Hz to 100 kHz (CCW/CW or pulse plus direction)</span></span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,sans-serif"><span style="background-color:white"><span style="color:#2980b9">Ng&otilde; ra xung điều chế&nbsp; (PWM): Duty ratio: 0.0% to 100.0% (specified in increments of 0.1% or 1%)</span></span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,sans-serif"><span style="background-color:white"><span style="color:#2980b9">C&oacute; thể mở rộng th&ecirc;m cổng truyền th&ocirc;ng RS232C hoặc RS422/RS485</span></span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,sans-serif"><span style="background-color:white"><span style="color:#2980b9">Trang bị đồng hồ thực (clock)</span></span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,sans-serif"><span style="background-color:white"><span style="color:#2980b9">Ti&ecirc;u chuẩn: EC, EMC (zone B), JIS</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>', '<ul>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">PLC n