	(6, 'S8FS-C', 'S8FS-C', NULL, '<ul>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Bộ nguồn tổ ong với thiết kế mới, độ bền cao, ti&ecirc;u chuẩn to&agrave;n cầu</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Điện &aacute;p&nbsp;v&agrave;o 100-120VAC hoặc 200-240VAC </span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">C&ocirc;ng suất đa dạng từ 25W, 35W, 50W, 75W, 100W, 150W, 200W, 350W.</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Điện &aacute;p ra : 5V, 12V, 15V, 24V, 36V, 48V</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Phụ kiện Din S82Y-FSC* lắp bộ nguồn l&ecirc;n thanh ray b&aacute;n ri&ecirc;ng theo y&ecirc;u cầu kh&aacute;ch h&agrave;ng</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Khả năng bảo vệ qu&aacute; &aacute;p bất thường . Điện &aacute;p đầu ra ổn định ngay cả đầu v&agrave;o kh&ocirc;ng ổn định.</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Linh kiện trong bộ nguồn S8FS được nhập từ Nhật Bản cho chất lượng hoạt động ổn định v&agrave; độ bền cao nhất.</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Tu&acirc;n thủ theo ti&ecirc;u chuẩn CE , UL , CCc</span></span></li>\r\n	<li><span style="font-size:14px"><a href="https://drive.google.com/open?id=1SOVbVG6Kr7UHqFwwRHqGrFkr7luO-uz8"><span style="color:#f39c12">Download t&agrave;i liệu</span></a></span></li>\r\n</ul>', 5, 1, '/uploads/cke/S8FS.jpg', 'https://phanthinh.com.vn', 1, '2018-09-08 13:29:51', '2018-09-23 04:03:52', 0, 1),
	(7, 'S8VK-C', 'S8VK-C', '<p>&nbsp;</p>\r\n\r\n<ul>\r\n</ul>', '<ul>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Nguồn cấp:&nbsp; 100-240VAC, 50/60 Hz hoặc 90-350VDC</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Nguồn ra c&oacute; thể điều chỉnh:&nbsp; &ndash;10% ~ 15% (V.ADJ)</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Ng&otilde; ra ảnh hưởng bởi nhiệt độ: 0.05%/&deg;C max.</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Bảo vệ qu&aacute; tải: 121% ~ 160% d&ograve;ng định mức</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Chức năng mắc song song: C&oacute;</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Cho ph&eacute;p mắc nối tiếp 2 bộ nguồn (gắn th&ecirc;m diode ngo&agrave;i)</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Nhiệt độ l&agrave; việc: &ndash;40 ~ 70&deg;C</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">C&aacute;ch ly ng&otilde; v&agrave;o /ra: 100 M&Omega; min. với điện &aacute;p 500VDC</span></span></li>\r\n	<li><span style="font-size:14px"><span style="color:#2980b9">Ti&ecirc;u chuẩn: UL508/60950-1, CSA C22.2 No. 107.1/60950-1 EN50178 (= VDE0160), EN60950-1 (= VDE0805). Lloyd&rsquo;s standards *2, EN60204-1 PELV Safety of Power Transformers: EN61558-2-16</span></span></li>\r\n	<li><span style="font-size:14px"><a href="https://drive.google.com/open?id=0B5JT8gQq5SdlZGtrZWRsT28wNWM"><span style="color:#f39c12">Download t&agrave;i liệu</span></a></span></li>\r\n</ul>', 5, 1, '/uploads/cke/S8VK.jpg', 'https://phanthinh.com.vn', 2, '2018-09-08 13:37:25', '2018-09-23 03:42:36', 0, 1),
