	(28, 'H7EC*/H7ET*', 'H7EC', NULL, '<p><strong><span style="color:#2980b9"><span style="font-size:14px">Bộ đếm&nbsp;sản phẩm&nbsp;H7EC-N:</span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">T&aacute;m chữ số, dải đếm từ 0 đến 99999999; C&oacute; sẵn pin với tuổi thọ 7 năm bảy chữ số</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">K&iacute;ch thước 24 x 48 mm</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Tốc độ của đầu v&agrave;o k&eacute;p: 30 Hz 1 kHz (trừ c&aacute;c model đầu v&agrave;o đa điện &aacute;p AC/DC)</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Đầu v&agrave;o đếm: đầu v&agrave;o PNP/NPN điện &aacute;p DC, đầu v&agrave;o đa điện &aacute;p AC/DC, đầu v&agrave;o kh&ocirc;ng điện &aacute;p</span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style="color:#2980b9"><span style="font-size:14px">Bộ đếm thời gian hoạt động của m&aacute;y H7ET</span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">C&oacute; sẵn pin với tuổi thọ 7 năm bảy chữ số, dải thời gian từ 999999,9h&nbsp; v&agrave; 3999d23.9h hoặc 999h59m59s&nbsp;v&agrave; 9999h59.9m.</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Đầu v&agrave;o đếm thời gian : đầu v&agrave;o PNP/NPN điện &aacute;p DC, đầu v&agrave;o đa điện &aacute;p AC/DC, đầu v&agrave;o kh&ocirc;ng điện &aacute;p</span></span></li>\r\n	<li><span style="font-size:14px"><a href="https://drive.google.com/open?id=1yZcyLUwXEWun3wfZ6ANeB7OxhsgZ6RCR"><span style="color:#f39c12">Download t&agrave;i liệu</span></a></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 8, 1, '/uploads/cke/H7ET.png', 'https://phanthinh.com.vn', 3, '2018-10-13 08:29:20', '2018-10-13 09:07:25', 0, 1),
	(29, 'E2E (Small Diameter)', 'E2E', '<ul>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">V&acirc;̣t ph&aacute;t hiện chu&acirc;̉n :&nbsp;Sắt&nbsp;Iron 3 &times; 3 &times; 1 mm, 6 &times; 6 &times; 1 mm, 4 &times; 4 &times; 1 mm, 9 &times; 9 &times; 1 mm,&nbsp;</span></span><span style="color:#2980b9"><span style="font-size:14px">6.5 &times;6.5 &times;1 mm</span></span>,&nbsp;<span style="color:#2980b9"><span style="font-size:14px">12 &times;12 &times;1m&nbsp;(Phụ thuộc v&agrave;o từng model)</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Tần số đ&aacute;p ứng : 5&nbsp;kHz, 4&nbsp;kHz, 3.5&nbsp;kHz, 2&nbsp;kHz</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Ng&otilde; ra điều khiển :&nbsp;DC 3-wire PNP open-collector output,&nbsp;DC 3-wire NPN open-collector output,&nbsp;DC 3-wire NPN collector load built-in output&nbsp;,&nbsp;DC 3-wire PNP collector load built-in output&nbsp;</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Ứng dụng của Cảm biến từ Omron E2E: Sử dụng để ph&aacute;t hiện kim loại c&oacute; từ t&iacute;nh, c&aacute;c ứng dụng c&oacute; nhiệt độ cao hoặc thấp, c&aacute;c ứng dụng di chuyển.</span></span></li>\r\n</ul>', '<ul>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px"><strong>Cảm biến từ Omron E2E</strong>&nbsp;l&agrave; d&ograve;ng cảm biến ti&ecirc;u chuẩn để ph&aacute;t hiện c&aacute;c kim loại c&oacute; từ t&iacute;nh.</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Hình trụ&nbsp;đường k&iacute;nh 3, 4, 5, 6.5, M4, M5,&nbsp;</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Ngu&ocirc;̀n c&acirc;́p&nbsp;:&nbsp;12-24VDC (10-30VDC)</span></span></li>\r\n	<li><span style="color:#2980b9"><span style="font-size:14px">Nhiều model với c&aacute;c tần số kh&aacute;c nhau để ngăn chặn c&aacute;c nhiễu ảnh hưởng lẫn nhau.</span></span></li>\r\